mapper.select:
SELECT * from food where 1=1

	and name=#{name}


	and id !=#{myid}

mapper.insert:
update food set 
name=#{name},price=#{price},pic=#{file_path},dscri=#{dscri},update_time=#{update_time},
type=#{type}
where 
id = #{food_id}

mapper.select:
select * from (SELECT ordering.*,evaluate.content,evaluate.reply, GROUP_CONCAT(CONCAT(food.name,'(',order_food.num),')') AS "foods" FROM ordering LEFT JOIN order_food ON ordering.id = order_food.order_id LEFT JOIN food ON order_food.food_id = food.id LEFT JOIN evaluate ON order_food.order_id = evaluate.order_id where 1=1
and DATE_FORMAT(NOW(),'%Y%m%d') like DATE_FORMAT(ordering.cerate_time,'%Y%m%d')
GROUP BY ordering.id ) A where A.user_id = #{front_userId}
